window.onload=function(){document.body.classList.add("loaded")};let listSort=document.querySelector(".concert__btn-list"),btnSort=document.querySelectorAll(".concert__btn"),itemSort=document.querySelectorAll(".concert__item");function showConcert(t){let e=t.getAttribute("id").replace(/btn-/,""),o=document.querySelector("."+e);for(let t=0;t<itemSort.length;t++)itemSort[t].classList.remove("disabled");if(!t.classList.contains("active")){for(let t=0;t<btnSort.length;t++)btnSort[t].classList.remove("active");if(t.classList.add("active"),!t.classList.contains("btn-world"))for(let t=0;t<itemSort.length;t++)o!=itemSort[t]&&itemSort[t].classList.add("disabled")}}listSort.addEventListener("click",(function(){let t=event.target;"BUTTON"==t.tagName&&showConcert(t)}));